sp_widget.script:
(function() {
	//Check current time against opening and closing times.
  var open = new GlideDateTime("1970-01-01 08:00:00")
  var close = new GlideDateTime("1970-01-02 20:00:00");	
	var now = new GlideDateTime("1970-01-01 19:01:00").getUserFormattedLocalTime();

	open = open.getTime().getByFormat("HH:mm:ss");
	close = close.getTime().getByFormat("HH:mm:ss");

	console.log(open);
	console.log(close);
	console.log(now);

	if(now >= open && now <= close) { 
		console.log(true);
	} else { 
		console.log(false);
	}
})();
